pragma solidity ^0.4.18;

contract Plasma {
    // Explain that the current model is run by a single operator but could PoS
    // Explain the role of the operator in terms submitting blocks ect..
    address public operator;

    // Challenge period is the period an exit must wait until it's submitted to the main chain (Ethereum)
    // Set up front on contract creation
    /* uint constant public CHALLENGE_PERIOD; */

    // Bond that is attached to each exit in the case of a challenge. Bond is returned to exit owner if no successful challenges
    /* uint constant public EXIT_BOND; */

    // Incremented each time a block is added to the Plasma Chain
    // A block can't be later re-written
    uint public currentPlasmaBlockNumber;

    // Block number to PlasmaBlock
    // Should only be modified when submitBlock is called
    mapping (uint => PlasmaBlock) public plasmaChain;

    // Exit ID to PlasmaExit
    // Should only be modified when users start or challenge exits
    // Why would this be modified when challenge exits?
    mapping (uint => PlasmaExit) public plasmaExits;

    // Is this an external library?
    // Could probably jsut import this if its too complex
    /* PriorityQueue exitQueue; */

    // Indexed lets you filter by that param when later searching through events
    event DepositCreated(
        address indexed owner,
        uint amount,
        uint blockNumber
    );

    // bytes32 is the how the data of a hex or hash should be stored
    event BlockSubmitted(
        uint blockNumber,
        bytes32 blockRoot
    );

    // blockNumber of block in which UTXO is held?
    // outputIndex: index of UTXO?
    event ExitStarted(
        address indexed owner,
        uint blockNumber,
        uint txIndex,
        uint outputIndex,
        uint amount
    );

    // The actual plasma block that is created and submitted by the operator(s).
    // This includes a merkle root as well as the timestamp it was submitted
    // Explain Merkle Root and how it helps to confirm the validity of transactions
    // Could refer to our Merkle lesson
    struct PlasmaBlock {
        bytes32 root;
        uint timestamp;
    }

    // Block of data that waits to be submitted post challenge period
    // How does this struct interact with the UTXO(s) within the Plasma Chain?
    struct PlasmaExit {
        address owner;
        uint amount;
        bool isActive;
        bool isBlocked;
    }

    // Constructor function that sets the operator, challenge period, and exit bond
    // What are the current implementation for exit bonds
    function Plasma() {
        operator = msg.sender;
        /* CHALLENGE_PERIOD = _challengePeriod;
        EXIT_BOND = _exitBond; */
        currentPlasmaBlockNumber = 1;
    }

    // Allows any user to deposit funds into the contract
    // Tough to understand right away what is supposed to be submitted to PlasmaBlock
    // Block is created here and added to the child Chain

    // Not mentioned: how to calculate the root and what it means in this instance, adding the block to the plasmachain, how the actual child chain picks up on this addition
    function deposit() public payable returns(uint blockNumber) {
        bytes32 root = keccak256(msg.sender, msg.value);
        PlasmaBlock memory plasmaBlock = PlasmaBlock(root, block.timestamp);
        plasmaChain[currentPlasmaBlockNumber] = plasmaBlock;
        DepositCreated(msg.sender, msg.value, currentPlasmaBlockNumber);
        currentPlasmaBlockNumber++;
    }

    // Let operator submit the block to the rootchain
    // blockRoot is calculated beforehand and passed into the function
    // blockRoot is the root hash of the Merkle tree of the transactions in the block
    // How are previous transactions considered? Is this only the root of transactions within this specific block?
    // Does it matter if the currentBlock is iterated before or after the event?
    function submitBlock(bytes32 _blockRoot) public {
        require(msg.sender == operator);
        PlasmaBlock memory plasmaBlock = PlasmaBlock(_blockRoot, block.timestamp);
        plasmaChain[currentPlasmaBlockNumber] = plasmaBlock;
        BlockSubmitted(currentPlasmaBlockNumber, _blockRoot);
        currentPlasmaBlockNumber++;
    }

    // EXIT LOGIC BEGINS

    // Allows any user to attempt to withdraw funds from the contract by pointing to a tx output
    // @param _txoBlockNumber: Block number in which the tx output was created
    // @param _txoTxIndex: Index of the transaction inside the block
    // @param _txoOutputIndex: index of the output inside the transactions (either 0 or 1)
    // Notes: What does this mean being 0 or 1? What exactly is this above mapping to?
    // @param _encodedTx: RLP encoded transaction that created the output
    // Notes: Should explain what RLP encoding is and how its used in this context and ethereum transaction in general
    // @param _txInclusionProof: Merkle proof that _encodedTx was included at _txoBlockNumber and _txoTxIndex
    // Notes: Is this any different from the normal merkle proof that we would need to provide? Is the user supposed to maintain thse proofs for each transaction? Is the user supposed to be a continual validator of the plasma chain?
    // @param _txSignatures: initial signatures by the owners of each input to the transactions
    // @param _txConfirmationSignatures: Confirmation signatures by the owners of each input to the transaction
    function startExit(
        uint _txoBlockNumber,
        uint _txoTxIndex,
        uint _txoOutputIndex,
        bytes _encodedTx,
        bytes _txInclusionProof,
        bytes _txSignatures,
        bytes _txConfirmationSignatures
    ) public payable returns (bool success)
    {
        // how to check use _txInclusionProof to check that _encodedTx was included in the Plasma chain?
        // how to check the output of _encodedTx given by _txoOutputIndex is owned by msg.sender?

        // is block number the current block number or the tx block number?
        ExitStarted(msg.sender, _txoBlockNumber, _txoTxIndex, _txoOutputIndex, msg.value);
    }

    // Allows any user to prove that a given exit is invalid

    function challengeExit(
        uint _exitingTxoBlockNumber,
        uint _exitingTxoTxIndex,
        uint _exitingTxoOutputIndex,
        bytes _encodedSpendingTx,
        bytes _spendingTxConfirmationSignature
    ) public returns (bool success)
    {
        // how to check that _encodedSpendingTx spends the specified output?
        // how to check that the _spendingTxConfirmationSignature is correctly signed by the owner of the PlasmaExit?

        // where is the PlasmaExit created that is then set to isBlock == true if above conditions pass
    }

    // Pays out any exits that have passed their challenge period
    function processExits() public returns (uint processed) {
        // how does the PlasmaExit interact with the exitQueue?
        // Assuming the exitQueue is filled with PlasmaExits and on each iteration we check for isBlocked?
        // Not fully explained when PlasmaExits are created and how they interact with PriorityQueue
